function checkValid(e){var t=/(ftp|http|https):\/\/(\w+:{0,1}\w*@)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/;return t.test(e)}function feedLoaded(e){if(!e.error){var t=document.getElementById("content");t.innerHTML="";for(var n=0;n<e.feed.entries.length;n++){var r=e.feed.entries[n];var i=document.createElement("article");var s=document.createElement("heading");var o=document.createElement("a");var u=document.createTextNode(r.title);o.appendChild(u);if(checkValid(r.content)){o.href=r.content}else{o.href=r.link}s.appendChild(o);i.appendChild(s);t.appendChild(i)}}}function OnLoad(){var e=new google.feeds.Feed("https://news.layervault.com/?format=rss");e.setNumEntries(20);e.load(feedLoaded)}google.load("feeds","1");google.setOnLoadCallback(OnLoad);var nodes=document.querySelectorAll("#logo, #refresh");for(var i=0;i<nodes.length;i++){nodes[i].addEventListener("click",function(e){location.reload()})}